import React, { useState, useEffect } from 'react';
import { useQuiz } from '../contexts/QuizContext';
import { useProgress } from '../contexts/ProgressContext';

const AdvancedQuiz = ({ quizData, onComplete }) => {
  const { 
    activeQuiz, 
    quizAnswers, 
    showFeedback, 
    currentQuestionIndex, 
    answeredQuestions,
    startQuiz, 
    submitAnswer, 
    finishQuiz, 
    getQuizProgress,
    getCurrentQuestion
  } = useQuiz();
  
  const { markTopicComplete } = useProgress();
  const [timeRemaining, setTimeRemaining] = useState(quizData.timeLimit || 3600); // 60 minutes default
  const [showResults, setShowResults] = useState(false);
  const [startTime] = useState(Date.now());
  const [quizResults, setQuizResults] = useState(null);

  useEffect(() => {
    if (!activeQuiz) {
      startQuiz(quizData);
    }
  }, [activeQuiz, quizData, startQuiz]);

  useEffect(() => {
    if (timeRemaining > 0 && activeQuiz && !showResults) {
      const timer = setTimeout(() => setTimeRemaining(timeRemaining - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeRemaining <= 0 && activeQuiz) {
      handleQuizComplete();
    }
  }, [timeRemaining, activeQuiz, showResults]);

  const handleAnswerSelect = (answerIndex) => {
    const currentQuestion = getCurrentQuestion();
    if (!currentQuestion || showFeedback) return;
    
    const isCorrect = answerIndex === currentQuestion.correctAnswer;
    submitAnswer(currentQuestion.id, answerIndex, isCorrect);
  };



  // Check if we should auto-complete the quiz
  useEffect(() => {
    if (currentQuestionIndex >= activeQuiz.questions.length - 1 && 
        showFeedback && 
        answeredQuestions.size === activeQuiz.questions.length) {
      // All questions answered, complete quiz
      setTimeout(() => {
        handleQuizComplete();
      }, 1000);
    }
  }, [currentQuestionIndex, showFeedback, answeredQuestions, activeQuiz]);

  const handleQuizComplete = () => {
    const endTime = Date.now();
    const timeTaken = Math.floor((endTime - startTime) / 1000);
    
    const totalQuestions = activeQuiz.questions.length;
    const correctAnswers = Object.values(quizAnswers).filter(answer => answer.isCorrect).length;
    const score = Math.round((correctAnswers / totalQuestions) * 100);
    const grade = calculateGrade(score);
    
    const results = {
      score,
      correctAnswers,
      totalQuestions,
      timeTaken,
      grade,
      answers: quizAnswers,
      questions: activeQuiz.questions,
      passed: score >= 70
    };
    
    setQuizResults(results);
    setShowResults(true);
    markTopicComplete(activeQuiz.topicId || 'general-quiz', score);
    
    if (onComplete) {
      onComplete(results);
    }
  };

  const calculateGrade = (score) => {
    if (score >= 90) return 'A+ (Excellent)';
    if (score >= 80) return 'A (Very Good)';
    if (score >= 70) return 'B (Good)';
    if (score >= 60) return 'C (Average)';
    if (score >= 50) return 'D (Below Average)';
    return 'F (Needs Improvement)';
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getAnswerLetter = (index) => String.fromCharCode(65 + index); // A, B, C, D

  if (showResults) {
    return <QuizResults results={quizResults} onRestart={() => {
      setShowResults(false);
      setQuizResults(null);
      startQuiz(quizData);
    }} />;
  }

  if (!activeQuiz || !getCurrentQuestion()) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-dark-bg flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-blue mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-300">Loading quiz...</p>
        </div>
      </div>
    );
  }

  const currentQuestion = getCurrentQuestion();
  const progress = getQuizProgress();

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-bg py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Quiz Header */}
        <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 mb-6">
          <div className="flex flex-wrap justify-between items-center gap-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                {activeQuiz.title}
              </h1>
              <p className="text-gray-600 dark:text-gray-300">
                {activeQuiz.description}
              </p>
            </div>
            
            <div className="flex flex-wrap gap-4">
              <div className="bg-blue-100 dark:bg-blue-900/30 px-4 py-2 rounded-lg">
                <div className="text-sm text-gray-600 dark:text-gray-300">Time Remaining</div>
                <div className="text-xl font-bold text-blue-600 dark:text-blue-400">
                  {formatTime(timeRemaining)}
                </div>
              </div>
              
              <div className="bg-green-100 dark:bg-green-900/30 px-4 py-2 rounded-lg">
                <div className="text-sm text-gray-600 dark:text-gray-300">Progress</div>
                <div className="text-xl font-bold text-green-600 dark:text-green-400">
                  {currentQuestionIndex + 1}/{activeQuiz.questions.length}
                </div>
              </div>
              
              <div className="w-full md:w-auto">
                <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-primary-blue to-primary-green h-2 rounded-full transition-all duration-300"
                    style={{ width: `${progress}%` }}
                  ></div>
                </div>
                <div className="text-center text-sm text-gray-600 dark:text-gray-300 mt-1">
                  {progress}% Complete
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Question Card */}
        <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-8 mb-6">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary-blue rounded-full flex items-center justify-center text-white font-bold">
                {currentQuestionIndex + 1}
              </div>
              <span className="text-sm text-gray-500 dark:text-gray-400">
                Question {currentQuestionIndex + 1} of {activeQuiz.questions.length}
              </span>
            </div>
            
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${
              currentQuestion.difficulty === 'Easy' 
                ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400' 
                : currentQuestion.difficulty === 'Medium' 
                ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-400' 
                : 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-400'
            }`}>
              {currentQuestion.difficulty}
            </div>
          </div>

          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
            {currentQuestion.question}
          </h2>

          {currentQuestion.image && (
            <div className="mb-6">
              <img 
                src={currentQuestion.image} 
                alt="Question illustration" 
                className="max-w-full h-auto rounded-lg shadow-md mx-auto"
              />
            </div>
          )}

          {/* Options */}
          <div className="space-y-4">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(index)}
                disabled={showFeedback}
                className={`w-full text-left p-6 rounded-2xl border-3 transition-all duration-500 transform hover:scale-[1.02] hover:shadow-2xl ${
                  showFeedback
                    ? index === currentQuestion.correctAnswer
                      ? 'border-4 border-green-500 bg-gradient-to-r from-green-100 via-green-50 to-emerald-100 dark:from-green-900/40 dark:via-green-800/30 dark:to-emerald-900/40 text-green-900 dark:text-green-100 shadow-2xl animate-pulse'
                      : quizAnswers[currentQuestion.id]?.answer === index
                      ? 'border-4 border-red-500 bg-gradient-to-r from-red-100 via-red-50 to-rose-100 dark:from-red-900/40 dark:via-red-800/30 dark:to-rose-900/40 text-red-900 dark:text-red-100 shadow-2xl'
                      : 'border-gray-300 dark:border-gray-600 bg-gray-100 dark:bg-gray-800/60 text-gray-600 dark:text-gray-400 opacity-60'
                    : 'border-2 border-gray-300 dark:border-gray-600 hover:border-purple-500 bg-gradient-to-r from-white to-gray-50 dark:from-gray-800 dark:to-gray-700 hover:from-purple-50 hover:to-indigo-50 dark:hover:from-purple-900/20 dark:hover:to-indigo-900/20 text-gray-800 dark:text-gray-200 shadow-lg hover:shadow-2xl'
                }`}
              >
                <div className="flex items-center">
                  <div className={`w-12 h-12 rounded-full flex items-center justify-center mr-5 font-bold text-xl transition-all duration-300 ${
                    showFeedback
                      ? index === currentQuestion.correctAnswer
                        ? 'bg-gradient-to-br from-green-500 to-emerald-600 text-white shadow-lg animate-bounce'
                        : quizAnswers[currentQuestion.id]?.answer === index
                        ? 'bg-gradient-to-br from-red-500 to-rose-600 text-white shadow-lg'
                        : 'bg-gray-400 dark:bg-gray-600 text-gray-600 dark:text-gray-300'
                      : 'bg-gradient-to-br from-blue-500 to-purple-600 text-white shadow-md hover:shadow-lg hover:scale-110'
                  }`}>
                    {getAnswerLetter(index)}
                  </div>
                  <span className="text-xl font-semibold">{option}</span>
                </div>
              </button>
            ))}
          </div>

          {/* Feedback */}
          {showFeedback && (
            <div className={`mt-8 p-6 rounded-2xl border-l-8 transition-all duration-500 animate-fadeIn ${
              quizAnswers[currentQuestion.id]?.isCorrect
                ? 'bg-gradient-to-r from-green-100 via-emerald-50 to-teal-100 dark:from-green-900/30 dark:via-emerald-800/20 dark:to-teal-900/30 border-green-500 text-green-800 dark:text-green-200 shadow-2xl'
                : 'bg-gradient-to-r from-red-100 via-rose-50 to-pink-100 dark:from-red-900/30 dark:via-rose-800/20 dark:to-pink-900/30 border-red-500 text-red-800 dark:text-red-200 shadow-2xl'
            }`}>
              <div className="flex items-center mb-3">
                <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-3 font-bold text-xl ${
                  quizAnswers[currentQuestion.id]?.isCorrect
                    ? 'bg-gradient-to-br from-green-500 to-emerald-600 text-white animate-bounce'
                    : 'bg-gradient-to-br from-red-500 to-rose-600 text-white'
                }`}>
                  {quizAnswers[currentQuestion.id]?.isCorrect ? 'âœ“' : 'âœ—'}
                </div>
                <div className="text-2xl font-bold">
                  {quizAnswers[currentQuestion.id]?.isCorrect ? 'Correct! ðŸŽ‰' : 'Incorrect âŒ'}
                </div>
              </div>
              <div className="text-lg leading-relaxed mb-4">
                {currentQuestion.explanation}
              </div>
              {!quizAnswers[currentQuestion.id]?.isCorrect && (
                <div className="mt-4 p-4 bg-white/50 dark:bg-black/20 rounded-xl">
                  <div className="font-semibold text-lg mb-2 flex items-center">
                    <span className="mr-2">âœ…</span>
                    Correct Answer:
                  </div>
                  <div className="text-lg font-medium">
                    {getAnswerLetter(currentQuestion.correctAnswer)}. {currentQuestion.options[currentQuestion.correctAnswer]}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Navigation */}
        <div className="flex justify-between mt-8">
          <button
            onClick={() => {}}
            disabled={currentQuestionIndex === 0}
            className="px-8 py-4 bg-gradient-to-r from-gray-300 to-gray-400 dark:from-gray-600 dark:to-gray-700 text-gray-700 dark:text-gray-300 rounded-xl font-semibold disabled:opacity-40 disabled:cursor-not-allowed transition-all duration-300 transform hover:scale-105 hover:shadow-lg"
          >
            â† Previous
          </button>
          
          {currentQuestionIndex === activeQuiz.questions.length - 1 ? (
            <button
              onClick={handleQuizComplete}
              className="px-8 py-4 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white rounded-xl font-semibold transition-all duration-300 transform hover:scale-105 hover:shadow-2xl animate-pulse"
            >
              ðŸŽ‰ Finish Quiz
            </button>
          ) : (
            <button
              onClick={handleNextQuestion}
              disabled={!showFeedback}
              className={`px-8 py-4 rounded-xl font-semibold transition-all duration-300 transform hover:scale-105 hover:shadow-2xl ${
                showFeedback 
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white animate-pulse'
                  : 'bg-gradient-to-r from-gray-400 to-gray-500 dark:from-gray-500 dark:to-gray-600 text-gray-500 dark:text-gray-400 cursor-not-allowed'
              }`}
            >
              {showFeedback ? 'Next Question â†’' : 'Answer to Continue â†’'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

const QuizResults = ({ results, onRestart }) => {
  const {
    score,
    correctAnswers,
    totalQuestions,
    timeTaken,
    grade,
    answers,
    questions,
    passed
  } = results;

  const formatTimeTaken = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins} min ${secs} sec`;
  };

  const getPerformanceColor = (percentage) => {
    if (percentage >= 90) return 'text-green-600';
    if (percentage >= 70) return 'text-blue-600';
    if (percentage >= 50) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-bg py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Results Header */}
        <div className={`rounded-2xl shadow-2xl p-10 mb-10 text-center transform transition-all duration-700 hover:scale-[1.02] ${
          passed 
            ? 'bg-gradient-to-r from-green-500 via-emerald-500 to-teal-500 text-white animate-pulse' 
            : 'bg-gradient-to-r from-red-500 via-rose-500 to-pink-500 text-white'
        }`}>
          <div className="text-8xl font-bold mb-6 animate-bounce">
            {score}%
          </div>
          <div className="text-3xl font-bold mb-4">{grade}</div>
          <div className={`text-2xl font-semibold ${passed ? 'text-green-100' : 'text-red-100'}`}>
            {passed ? 'ðŸŽ‰ Congratulations! You Passed! ðŸŽŠ' : 'ðŸ“š Keep studying and try again! ðŸ’ª'}
          </div>
          <div className="mt-6 text-lg opacity-90">
            {passed ? 'You\'ve demonstrated excellent knowledge!' : 'Review the explanations and try again.'}
          </div>
        </div>

        {/* Statistics */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
          <div className="bg-gradient-to-br from-green-100 to-emerald-100 dark:from-green-900/30 dark:to-emerald-900/30 rounded-2xl shadow-xl p-8 text-center transform transition-all duration-300 hover:scale-105 hover:shadow-2xl">
            <div className="text-5xl font-bold text-green-600 dark:text-green-400 mb-3">âœ“</div>
            <div className="text-4xl font-bold text-green-700 dark:text-green-300 mb-2">{correctAnswers}</div>
            <div className="text-lg font-semibold text-green-600 dark:text-green-400">Correct Answers</div>
          </div>
          
          <div className="bg-gradient-to-br from-red-100 to-rose-100 dark:from-red-900/30 dark:to-rose-900/30 rounded-2xl shadow-xl p-8 text-center transform transition-all duration-300 hover:scale-105 hover:shadow-2xl">
            <div className="text-5xl font-bold text-red-600 dark:text-red-400 mb-3">âœ—</div>
            <div className="text-4xl font-bold text-red-700 dark:text-red-300 mb-2">{totalQuestions - correctAnswers}</div>
            <div className="text-lg font-semibold text-red-600 dark:text-red-400">Incorrect Answers</div>
          </div>
          
          <div className="bg-gradient-to-br from-purple-100 to-indigo-100 dark:from-purple-900/30 dark:to-indigo-900/30 rounded-2xl shadow-xl p-8 text-center transform transition-all duration-300 hover:scale-105 hover:shadow-2xl">
            <div className="text-5xl font-bold text-purple-600 dark:text-purple-400 mb-3">â±ï¸</div>
            <div className="text-4xl font-bold text-purple-700 dark:text-purple-300 mb-2">{formatTimeTaken(timeTaken)}</div>
            <div className="text-lg font-semibold text-purple-600 dark:text-purple-400">Time Taken</div>
          </div>
          
          <div className="bg-gradient-to-br from-blue-100 to-cyan-100 dark:from-blue-900/30 dark:to-cyan-900/30 rounded-2xl shadow-xl p-8 text-center transform transition-all duration-300 hover:scale-105 hover:shadow-2xl">
            <div className="text-5xl font-bold text-blue-600 dark:text-blue-400 mb-3">ðŸŽ¯</div>
            <div className={`text-4xl font-bold ${getPerformanceColor(score)} mb-2`}>
              {Math.round((correctAnswers / totalQuestions) * 100)}%
            </div>
            <div className="text-lg font-semibold text-blue-600 dark:text-blue-400">Accuracy</div>
          </div>
        </div>

        {/* Detailed Review */}
        <div className="bg-gradient-to-br from-white to-gray-50 dark:from-card-dark dark:to-gray-800 rounded-2xl shadow-2xl p-10 mb-10">
          <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-8 text-center">
            ðŸ“š Detailed Review
          </h2>
          
          <div className="space-y-8">
            {questions.map((question, index) => {
              const userAnswer = answers[question.id];
              const isCorrect = userAnswer?.isCorrect;
              const userAnswerIndex = userAnswer?.answer;
              
              return (
                <div key={question.id} className={`p-8 rounded-2xl border-l-8 transition-all duration-500 transform hover:scale-[1.01] ${
                  isCorrect 
                    ? 'bg-gradient-to-r from-green-50 via-emerald-50 to-teal-50 dark:from-green-900/20 dark:via-emerald-900/20 dark:to-teal-900/20 border-green-500 shadow-xl' 
                    : 'bg-gradient-to-r from-red-50 via-rose-50 to-pink-50 dark:from-red-900/20 dark:via-rose-900/20 dark:to-pink-900/20 border-red-500 shadow-xl'
                }`}>
                  <div className="flex flex-wrap items-start justify-between gap-4 mb-6">
                    <div className="flex items-center space-x-4">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center font-bold text-xl shadow-lg ${
                        isCorrect 
                          ? 'bg-gradient-to-br from-green-500 to-emerald-600 text-white animate-bounce' 
                          : 'bg-gradient-to-br from-red-500 to-rose-600 text-white'
                      }`}>
                        {index + 1}
                      </div>
                      <h3 className="text-xl font-bold text-gray-900 dark:text-white">
                        {question.question}
                      </h3>
                    </div>
                    
                    <div className={`px-4 py-2 rounded-full text-lg font-bold ${
                      isCorrect 
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg' 
                        : 'bg-gradient-to-r from-red-500 to-rose-500 text-white shadow-lg'
                    }`}>
                      {isCorrect ? 'âœ“ Correct' : 'âœ— Incorrect'}
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                    <div className="bg-white/70 dark:bg-black/20 p-6 rounded-xl shadow-inner">
                      <div className="text-lg font-bold text-gray-700 dark:text-gray-300 mb-3 flex items-center">
                        <span className="mr-2">ðŸ‘¤</span>
                        Your Answer:
                      </div>
                      <div className={`p-4 rounded-lg text-lg font-semibold ${
                        isCorrect 
                          ? 'bg-green-200 dark:bg-green-900/40 text-green-800 dark:text-green-200' 
                          : 'bg-red-200 dark:bg-red-900/40 text-red-800 dark:text-red-200'
                      }`}>
                        {userAnswerIndex !== undefined 
                          ? `${String.fromCharCode(65 + userAnswerIndex)}. ${question.options[userAnswerIndex]}`
                          : 'No answer provided'
                        }
                      </div>
                    </div>
                    
                    {!isCorrect && (
                      <div className="bg-white/70 dark:bg-black/20 p-6 rounded-xl shadow-inner">
                        <div className="text-lg font-bold text-gray-700 dark:text-gray-300 mb-3 flex items-center">
                          <span className="mr-2">âœ…</span>
                          Correct Answer:
                        </div>
                        <div className="p-4 rounded-lg bg-green-200 dark:bg-green-900/40 text-green-800 dark:text-green-200 text-lg font-semibold">
                          {String.fromCharCode(65 + question.correctAnswer)}. {question.options[question.correctAnswer]}
                        </div>
                      </div>
                    )}
                  </div>
                  
                  <div className="bg-gradient-to-r from-blue-50 to-cyan-50 dark:from-blue-900/30 dark:to-cyan-900/30 p-6 rounded-xl border-2 border-blue-200 dark:border-blue-700">
                    <div className="text-lg font-bold text-blue-700 dark:text-blue-300 mb-3 flex items-center">
                      <span className="mr-2">ðŸ“š</span>
                      Explanation:
                    </div>
                    <div className="text-gray-800 dark:text-gray-200 text-lg leading-relaxed">{question.explanation}</div>
                  </div>
                  
                  <div className="mt-4 flex flex-wrap gap-2">
                    <div className="px-3 py-1 bg-gray-200 dark:bg-gray-700 rounded-full text-sm font-medium text-gray-700 dark:text-gray-300">
                      {question.category}
                    </div>
                    <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                      question.difficulty === 'Easy' 
                        ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400' 
                        : question.difficulty === 'Medium' 
                        ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-400' 
                        : 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-400'
                    }`}>
                      {question.difficulty}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Actions */}
        <div className="flex flex-col sm:flex-row gap-6 justify-center">
          <button
            onClick={onRestart}
            className="px-10 py-4 bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white rounded-2xl font-bold text-lg transition-all duration-300 transform hover:scale-105 hover:shadow-2xl animate-pulse"
          >
            ðŸ”„ Retake Quiz
          </button>
          
          <button
            onClick={() => window.print()}
            className="px-10 py-4 bg-gradient-to-r from-gray-300 to-gray-400 hover:from-gray-400 hover:to-gray-500 dark:from-gray-600 dark:to-gray-700 text-gray-700 dark:text-gray-300 rounded-2xl font-bold text-lg transition-all duration-300 transform hover:scale-105 hover:shadow-2xl"
          >
            ðŸ–¨ï¸ Print Results
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdvancedQuiz;
