import React, { useState } from 'react';
import { useProgress } from '../../contexts/ProgressContext';

const ArchitectureTypes = () => {
  const { markTopicComplete } = useProgress();
  const [completed, setCompleted] = useState(false);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Von Neumann vs Harvard Architecture</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Compare the two fundamental computer architecture designs and their differences
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
        <div className="module-card border-2 border-blue-500">
          <h2 className="text-2xl font-bold text-blue-600 dark:text-blue-400 mb-4">Von Neumann Architecture</h2>
          <ul className="space-y-2 text-gray-700 dark:text-gray-300">
            <li>• Single memory for both instructions and data</li>
            <li>• Sequential instruction execution</li>
            <li>• Simpler design, lower cost</li>
            <li>• Von Neumann bottleneck (memory access limitation)</li>
          </ul>
        </div>

        <div className="module-card border-2 border-green-500">
          <h2 className="text-2xl font-bold text-green-600 dark:text-green-400 mb-4">Harvard Architecture</h2>
          <ul className="space-y-2 text-gray-700 dark:text-gray-300">
            <li>• Separate memory for instructions and data</li>
            <li>• Parallel access to both memories</li>
            <li>• Higher performance, more complex</li>
            <li>• No Von Neumann bottleneck</li>
          </ul>
        </div>
      </div>

      <div className="text-center">
        <button
          onClick={() => {
            markTopicComplete('architecture-types');
            setCompleted(true);
          }}
          className={`btn-primary px-8 py-3 text-lg ${completed ? 'bg-green-500 hover:bg-green-600' : ''}`}
        >
          {completed ? '✓ Completed' : 'Mark as Complete'}
        </button>
      </div>
    </div>
  );
};

export default ArchitectureTypes;
