import React, { useState } from 'react';
import { useProgress } from '../../contexts/ProgressContext';

const DatabaseFundamentals = () => {
  const { markTopicComplete } = useProgress();
  const [completed, setCompleted] = useState(false);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Database Fundamentals & Architecture</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Learn the basics of databases, their architecture, and key concepts
        </p>
      </div>

      <div className="space-y-6">
        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">What is a Database?</h3>
          <p className="text-gray-700 dark:text-gray-300">
            A database is an organized collection of structured information, or data, typically stored 
            electronically in a computer system. A database is usually controlled by a database 
            management system (DBMS).
          </p>
        </div>

        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Database Architecture</h3>
          <ul className="space-y-2 text-gray-700 dark:text-gray-300">
            <li>• Three-level architecture: External, Conceptual, and Internal levels</li>
            <li>• Data independence: Physical and Logical independence</li>
            <li>• Schema separation for different user views</li>
          </ul>
        </div>
      </div>

      <div className="text-center mt-8">
        <button
          onClick={() => {
            markTopicComplete('database-fundamentals');
            setCompleted(true);
          }}
          className={`btn-secondary px-8 py-3 text-lg ${completed ? 'bg-green-500 hover:bg-green-600' : ''}`}
        >
          {completed ? '✓ Completed' : 'Mark as Complete'}
        </button>
      </div>
    </div>
  );
};

export default DatabaseFundamentals;
