import React, { useState } from 'react';
import { useProgress } from '../../contexts/ProgressContext';

const BinaryArithmetic = () => {
  const { markTopicComplete } = useProgress();
  const [completed, setCompleted] = useState(false);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Binary Arithmetic</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Learn how to perform arithmetic operations in binary number system
        </p>
      </div>

      <div className="space-y-6">
        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Binary Addition</h3>
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg font-mono">
            <div>  1011  (11 in decimal)</div>
            <div>+ 0110  (6 in decimal)</div>
            <div>------</div>
            <div> 10001  (17 in decimal)</div>
          </div>
        </div>

        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Binary Subtraction</h3>
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg font-mono">
            <div>  1011  (11 in decimal)</div>
            <div>- 0110  (6 in decimal)</div>
            <div>------</div>
            <div>  0101  (5 in decimal)</div>
          </div>
        </div>
      </div>

      <div className="text-center mt-8">
        <button
          onClick={() => {
            markTopicComplete('binary-arithmetic');
            setCompleted(true);
          }}
          className={`btn-primary px-8 py-3 text-lg ${completed ? 'bg-green-500 hover:bg-green-600' : ''}`}
        >
          {completed ? '✓ Completed' : 'Mark as Complete'}
        </button>
      </div>
    </div>
  );
};

export default BinaryArithmetic;
