import React from 'react';
import { Link } from 'react-router-dom';

const QuizCenter = () => {
  const quizzes = [
    {
      title: "Number Systems Quiz",
      description: "Test your knowledge of binary, decimal, octal, and hexadecimal systems",
      subject: "Computer Architecture",
      questions: 10,
      difficulty: "Beginner",
      path: "/quiz/number-systems",
      color: "bg-blue-500"
    },
    {
      title: "Database Fundamentals Quiz",
      description: "Assess your understanding of basic database concepts",
      subject: "SQL/DBMS",
      questions: 12,
      difficulty: "Beginner",
      path: "/quiz/database-fundamentals",
      color: "bg-green-500"
    },
    {
      title: "Keys & Normalization Quiz",
      description: "Challenge yourself with advanced database design concepts",
      subject: "SQL/DBMS",
      questions: 15,
      difficulty: "Advanced",
      path: "/quiz/keys-normalization",
      color: "bg-purple-500"
    }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Quiz Center</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Test your knowledge and track your progress with interactive quizzes
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {quizzes.map((quiz, index) => (
          <div key={index} className="module-card">
            <div className="flex items-start mb-4">
              <div className={`${quiz.color} w-12 h-12 rounded-lg flex items-center justify-center text-white text-xl mr-4`}>
                {index + 1}
              </div>
              <div>
                <h3 className="text-xl font-bold text-gray-900 dark:text-white">{quiz.title}</h3>
                <p className="text-gray-600 dark:text-gray-300 text-sm">{quiz.subject}</p>
              </div>
            </div>
            
            <p className="text-gray-700 dark:text-gray-300 mb-4">{quiz.description}</p>
            
            <div className="flex justify-between items-center text-sm text-gray-600 dark:text-gray-300 mb-4">
              <span>‚ùì {quiz.questions} questions</span>
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                quiz.difficulty === 'Beginner' ? 'bg-green-100 text-green-800' :
                quiz.difficulty === 'Intermediate' ? 'bg-yellow-100 text-yellow-800' :
                'bg-red-100 text-red-800'
              }`}>
                {quiz.difficulty}
              </span>
            </div>
            
            <Link 
              to={quiz.path}
              className="btn-primary px-4 py-2 text-sm w-full text-center"
            >
              Start Quiz
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizCenter;
