import React, { useState, useEffect } from 'react';
import { useQuiz } from '../contexts/QuizContext';
import { useProgress } from '../contexts/ProgressContext';

const AdvancedQuiz = ({ quizData, onComplete }) => {
  const { 
    activeQuiz, 
    quizAnswers, 
    showFeedback, 
    currentQuestionIndex, 
    answeredQuestions,
    startQuiz, 
    submitAnswer, 
    finishQuiz, 
    getQuizProgress,
    getCurrentQuestion
  } = useQuiz();
  
  const { markTopicComplete } = useProgress();
  const [timeRemaining, setTimeRemaining] = useState(quizData.timeLimit || 3600); // 60 minutes default
  const [showResults, setShowResults] = useState(false);
  const [startTime] = useState(Date.now());
  const [quizResults, setQuizResults] = useState(null);

  useEffect(() => {
    if (!activeQuiz) {
      startQuiz(quizData);
    }
  }, [activeQuiz, quizData, startQuiz]);

  useEffect(() => {
    if (timeRemaining > 0 && activeQuiz && !showResults) {
      const timer = setTimeout(() => setTimeRemaining(timeRemaining - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeRemaining <= 0 && activeQuiz) {
      handleQuizComplete();
    }
  }, [timeRemaining, activeQuiz, showResults]);

  const handleAnswerSelect = (answerIndex) => {
    const currentQuestion = getCurrentQuestion();
    if (!currentQuestion || showFeedback) return;
    
    const isCorrect = answerIndex === currentQuestion.correctAnswer;
    submitAnswer(currentQuestion.id, answerIndex, isCorrect);
  };

  const handleNextQuestion = () => {
    if (showFeedback && currentQuestionIndex < activeQuiz.questions.length - 1) {
      setShowFeedback(false);
      setCurrentQuestionIndex(prev => prev + 1);
    }
  };

  // Check if we should auto-complete the quiz
  useEffect(() => {
    if (currentQuestionIndex >= activeQuiz.questions.length - 1 && 
        showFeedback && 
        answeredQuestions.size === activeQuiz.questions.length) {
      // All questions answered, complete quiz
      setTimeout(() => {
        handleQuizComplete();
      }, 1000);
    }
  }, [currentQuestionIndex, showFeedback, answeredQuestions, activeQuiz]);

  const handleQuizComplete = () => {
    const endTime = Date.now();
    const timeTaken = Math.floor((endTime - startTime) / 1000);
    
    const totalQuestions = activeQuiz.questions.length;
    const correctAnswers = Object.values(quizAnswers).filter(answer => answer.isCorrect).length;
    const score = Math.round((correctAnswers / totalQuestions) * 100);
    const grade = calculateGrade(score);
    
    const results = {
      score,
      correctAnswers,
      totalQuestions,
      timeTaken,
      grade,
      answers: quizAnswers,
      questions: activeQuiz.questions,
      passed: score >= 70
    };
    
    setQuizResults(results);
    setShowResults(true);
    markTopicComplete(activeQuiz.topicId || 'general-quiz', score);
    
    if (onComplete) {
      onComplete(results);
    }
  };

  const calculateGrade = (score) => {
    if (score >= 90) return 'A+ (Excellent)';
    if (score >= 80) return 'A (Very Good)';
    if (score >= 70) return 'B (Good)';
    if (score >= 60) return 'C (Average)';
    if (score >= 50) return 'D (Below Average)';
    return 'F (Needs Improvement)';
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getAnswerLetter = (index) => String.fromCharCode(65 + index); // A, B, C, D

  if (showResults) {
    return <QuizResults results={quizResults} onRestart={() => {
      setShowResults(false);
      setQuizResults(null);
      startQuiz(quizData);
    }} />;
  }

  if (!activeQuiz || !getCurrentQuestion()) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-dark-bg flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-blue mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-300">Loading quiz...</p>
        </div>
      </div>
    );
  }

  const currentQuestion = getCurrentQuestion();
  const progress = getQuizProgress();

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-bg py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Quiz Header */}
        <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 mb-6">
          <div className="flex flex-wrap justify-between items-center gap-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                {activeQuiz.title}
              </h1>
              <p className="text-gray-600 dark:text-gray-300">
                {activeQuiz.description}
              </p>
            </div>
            
            <div className="flex flex-wrap gap-4">
              <div className="bg-blue-100 dark:bg-blue-900/30 px-4 py-2 rounded-lg">
                <div className="text-sm text-gray-600 dark:text-gray-300">Time Remaining</div>
                <div className="text-xl font-bold text-blue-600 dark:text-blue-400">
                  {formatTime(timeRemaining)}
                </div>
              </div>
              
              <div className="bg-green-100 dark:bg-green-900/30 px-4 py-2 rounded-lg">
                <div className="text-sm text-gray-600 dark:text-gray-300">Progress</div>
                <div className="text-xl font-bold text-green-600 dark:text-green-400">
                  {currentQuestionIndex + 1}/{activeQuiz.questions.length}
                </div>
              </div>
              
              <div className="w-full md:w-auto">
                <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-primary-blue to-primary-green h-2 rounded-full transition-all duration-300"
                    style={{ width: `${progress}%` }}
                  ></div>
                </div>
                <div className="text-center text-sm text-gray-600 dark:text-gray-300 mt-1">
                  {progress}% Complete
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Question Card */}
        <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-8 mb-6">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary-blue rounded-full flex items-center justify-center text-white font-bold">
                {currentQuestionIndex + 1}
              </div>
              <span className="text-sm text-gray-500 dark:text-gray-400">
                Question {currentQuestionIndex + 1} of {activeQuiz.questions.length}
              </span>
            </div>
            
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${
              currentQuestion.difficulty === 'Easy' 
                ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400' 
                : currentQuestion.difficulty === 'Medium' 
                ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-400' 
                : 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-400'
            }`}>
              {currentQuestion.difficulty}
            </div>
          </div>

          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
            {currentQuestion.question}
          </h2>

          {currentQuestion.image && (
            <div className="mb-6">
              <img 
                src={currentQuestion.image} 
                alt="Question illustration" 
                className="max-w-full h-auto rounded-lg shadow-md mx-auto"
              />
            </div>
          )}

          {/* Options */}
          <div className="space-y-4">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(index)}
                disabled={showFeedback}
                className={`w-full text-left p-5 rounded-xl border-2 transition-all duration-300 transform hover:scale-[1.02] ${
                  showFeedback
                    ? index === currentQuestion.correctAnswer
                      ? 'border-4 border-green-500 bg-gradient-to-r from-green-50 to-green-100 dark:from-green-900/30 dark:to-green-800/30 text-green-800 dark:text-green-200 shadow-lg'
                      : quizAnswers[currentQuestion.id]?.answer === index
                      ? 'border-4 border-red-500 bg-gradient-to-r from-red-50 to-red-100 dark:from-red-900/30 dark:to-red-800/30 text-red-800 dark:text-red-200 shadow-lg'
                      : 'border-gray-200 dark:border-gray-600 bg-gray-50 dark:bg-gray-800/50 text-gray-600 dark:text-gray-400 opacity-70'
                    : 'border-2 border-gray-200 dark:border-gray-600 hover:border-blue-500 bg-white dark:bg-gray-800 hover:bg-blue-50 dark:hover:bg-blue-900/20 text-gray-800 dark:text-gray-200 shadow-md hover:shadow-lg'
                }`}
              >
                <div className="flex items-center">
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-4 font-bold text-lg ${
                    showFeedback
                      ? index === currentQuestion.correctAnswer
                        ? 'bg-green-500 text-white'
                        : quizAnswers[currentQuestion.id]?.answer === index
                        ? 'bg-red-500 text-white'
                        : 'bg-gray-300 dark:bg-gray-600 text-gray-600 dark:text-gray-300'
                      : 'bg-blue-100 dark:bg-blue-900/50 text-blue-700 dark:text-blue-300'
                  }`}>
                    {getAnswerLetter(index)}
                  </div>
                  <span className="text-lg font-medium">{option}</span>
                </div>
              </button>
            ))}
          </div>

          {/* Feedback */}
          {showFeedback && (
            <div className={`mt-6 p-4 rounded-lg border-l-4 ${
              quizAnswers[currentQuestion.id]?.isCorrect
                ? 'bg-green-50 dark:bg-green-900/20 border-green-500 text-green-700 dark:text-green-300'
                : 'bg-red-50 dark:bg-red-900/20 border-red-500 text-red-700 dark:text-red-300'
            }`}>
              <div className="font-semibold mb-2">
                {quizAnswers[currentQuestion.id]?.isCorrect ? 'âœ“ Correct!' : 'âœ— Incorrect'}
              </div>
              <div className="text-sm">
                {currentQuestion.explanation}
              </div>
              {!quizAnswers[currentQuestion.id]?.isCorrect && (
                <div className="mt-2 text-sm">
                  <strong>Correct Answer:</strong> {getAnswerLetter(currentQuestion.correctAnswer)}. {
                    currentQuestion.options[currentQuestion.correctAnswer]
                  }
                </div>
              )}
            </div>
          )}
        </div>

        {/* Navigation */}
        <div className="flex justify-between">
          <button
            onClick={() => {}}
            disabled={currentQuestionIndex === 0}
            className="px-6 py-3 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-lg font-medium disabled:opacity-50"
          >
            Previous
          </button>
          
          {currentQuestionIndex === activeQuiz.questions.length - 1 ? (
            <button
              onClick={handleQuizComplete}
              className="px-6 py-3 bg-primary-green hover:bg-green-600 text-white rounded-lg font-medium transition-colors duration-200"
            >
              Finish Quiz
            </button>
          ) : (
            <button
              onClick={handleNextQuestion}
              disabled={!showFeedback}
              className="px-6 py-3 bg-primary-blue hover:bg-blue-600 text-white rounded-lg font-medium disabled:opacity-50 transition-colors duration-200"
            >
              {showFeedback ? 'Next Question' : 'Answer to Continue'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

const QuizResults = ({ results, onRestart }) => {
  const {
    score,
    correctAnswers,
    totalQuestions,
    timeTaken,
    grade,
    answers,
    questions,
    passed
  } = results;

  const formatTimeTaken = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins} min ${secs} sec`;
  };

  const getPerformanceColor = (percentage) => {
    if (percentage >= 90) return 'text-green-600';
    if (percentage >= 70) return 'text-blue-600';
    if (percentage >= 50) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-bg py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Results Header */}
        <div className={`rounded-xl shadow-lg p-8 mb-8 text-center ${
          passed 
            ? 'bg-gradient-to-r from-green-500 to-green-600 text-white' 
            : 'bg-gradient-to-r from-red-500 to-red-600 text-white'
        }`}>
          <div className="text-6xl font-bold mb-4">
            {score}%
          </div>
          <div className="text-2xl font-semibold mb-2">{grade}</div>
          <div className={`text-lg ${passed ? 'text-green-100' : 'text-red-100'}`}>
            {passed ? 'ðŸŽ‰ Congratulations! You Passed!' : 'ðŸ“š Keep studying and try again!'}
          </div>
        </div>

        {/* Statistics */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 text-center">
            <div className="text-3xl font-bold text-primary-blue mb-2">{correctAnswers}</div>
            <div className="text-gray-600 dark:text-gray-300">Correct Answers</div>
          </div>
          
          <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 text-center">
            <div className="text-3xl font-bold text-red-500 mb-2">{totalQuestions - correctAnswers}</div>
            <div className="text-gray-600 dark:text-gray-300">Incorrect Answers</div>
          </div>
          
          <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 text-center">
            <div className="text-3xl font-bold text-purple-500 mb-2">{formatTimeTaken(timeTaken)}</div>
            <div className="text-gray-600 dark:text-gray-300">Time Taken</div>
          </div>
          
          <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 text-center">
            <div className={`text-3xl font-bold ${getPerformanceColor(score)} mb-2`}>
              {Math.round((correctAnswers / totalQuestions) * 100)}%
            </div>
            <div className="text-gray-600 dark:text-gray-300">Accuracy</div>
          </div>
        </div>

        {/* Detailed Review */}
        <div className="bg-white dark:bg-card-dark rounded-xl shadow-lg p-8 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
            Detailed Review
          </h2>
          
          <div className="space-y-6">
            {questions.map((question, index) => {
              const userAnswer = answers[question.id];
              const isCorrect = userAnswer?.isCorrect;
              const userAnswerIndex = userAnswer?.answer;
              
              return (
                <div key={question.id} className={`p-6 rounded-lg border-l-4 ${
                  isCorrect 
                    ? 'bg-green-50 dark:bg-green-900/20 border-green-500' 
                    : 'bg-red-50 dark:bg-red-900/20 border-red-500'
                }`}>
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center space-x-3">
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold ${
                        isCorrect 
                          ? 'bg-green-500 text-white' 
                          : 'bg-red-500 text-white'
                      }`}>
                        {index + 1}
                      </div>
                      <h3 className="font-semibold text-gray-900 dark:text-white">
                        {question.question}
                      </h3>
                    </div>
                    
                    <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                      isCorrect 
                        ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400' 
                        : 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-400'
                    }`}>
                      {isCorrect ? 'Correct' : 'Incorrect'}
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <div className="text-sm text-gray-600 dark:text-gray-400 mb-2">Your Answer:</div>
                      <div className={`p-3 rounded ${
                        isCorrect 
                          ? 'bg-green-100 dark:bg-green-900/30 text-green-800 dark:text-green-300' 
                          : 'bg-red-100 dark:bg-red-900/30 text-red-800 dark:text-red-300'
                      }`}>
                        {userAnswerIndex !== undefined 
                          ? `${String.fromCharCode(65 + userAnswerIndex)}. ${question.options[userAnswerIndex]}`
                          : 'No answer provided'
                        }
                      </div>
                    </div>
                    
                    {!isCorrect && (
                      <div>
                        <div className="text-sm text-gray-600 dark:text-gray-400 mb-2">Correct Answer:</div>
                        <div className="p-3 rounded bg-green-100 dark:bg-green-900/30 text-green-800 dark:text-green-300">
                          {String.fromCharCode(65 + question.correctAnswer)}. {question.options[question.correctAnswer]}
                        </div>
                      </div>
                    )}
                  </div>
                  
                  <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg">
                    <div className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Explanation:</div>
                    <div className="text-gray-700 dark:text-gray-300">{question.explanation}</div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Actions */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <button
            onClick={onRestart}
            className="px-8 py-3 bg-primary-blue hover:bg-blue-600 text-white rounded-lg font-medium transition-colors duration-200"
          >
            Retake Quiz
          </button>
          
          <button
            onClick={() => window.print()}
            className="px-8 py-3 bg-gray-200 hover:bg-gray-300 dark:bg-gray-700 dark:hover:bg-gray-600 text-gray-700 dark:text-gray-300 rounded-lg font-medium transition-colors duration-200"
          >
            Print Results
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdvancedQuiz;
