import React, { createContext, useContext, useState } from 'react';

const QuizContext = createContext();

export const useQuiz = () => {
  const context = useContext(QuizContext);
  if (!context) {
    throw new Error('useQuiz must be used within a QuizProvider');
  }
  return context;
};

export const QuizProvider = ({ children }) => {
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [showFeedback, setShowFeedback] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answeredQuestions, setAnsweredQuestions] = useState(new Set());

  const startQuiz = (quizData) => {
    setActiveQuiz(quizData);
    setQuizAnswers({});
    setShowFeedback(false);
    setCurrentQuestionIndex(0);
    setAnsweredQuestions(new Set());
  };

  const submitAnswer = (questionId, answer, isCorrect) => {
    setQuizAnswers(prev => ({
      ...prev,
      [questionId]: { answer, isCorrect, timestamp: new Date().toISOString() }
    }));
    setAnsweredQuestions(prev => new Set([...prev, questionId]));
    setShowFeedback(true);
    
    // Auto-hide feedback after 3 seconds
    setTimeout(() => {
      setShowFeedback(false);
    }, 3000);
  };

  const finishQuiz = () => {
    // Calculate final score
    const totalQuestions = activeQuiz.questions.length;
    const correctAnswers = Object.values(quizAnswers).filter(answer => answer.isCorrect).length;
    const score = Math.round((correctAnswers / totalQuestions) * 100);
    
    // Trigger completion callback
    if (activeQuiz.onComplete) {
      activeQuiz.onComplete(score, quizAnswers);
    }
    
    // Reset quiz state
    setActiveQuiz(null);
    setQuizAnswers({});
    setCurrentQuestionIndex(0);
  };

  const resetQuiz = () => {
    setActiveQuiz(null);
    setQuizAnswers({});
    setShowFeedback(false);
    setCurrentQuestionIndex(0);
    setAnsweredQuestions(new Set());
  };

  const getQuizProgress = () => {
    if (!activeQuiz) return 0;
    return Math.round(((currentQuestionIndex + 1) / activeQuiz.questions.length) * 100);
  };

  const getCurrentQuestion = () => {
    if (!activeQuiz || currentQuestionIndex >= activeQuiz.questions.length) {
      return null;
    }
    return activeQuiz.questions[currentQuestionIndex];
  };

  const value = {
    activeQuiz,
    quizAnswers,
    showFeedback,
    currentQuestionIndex,
    answeredQuestions,
    startQuiz,
    submitAnswer,
    finishQuiz,
    resetQuiz,
    getQuizProgress,
    getCurrentQuestion
  };

  return (
    <QuizContext.Provider value={value}>
      {children}
    </QuizContext.Provider>
  );
};
