import React, { useState } from 'react';
import { useProgress } from '../../contexts/ProgressContext';

const ErDiagrams = () => {
  const { markTopicComplete } = useProgress();
  const [completed, setCompleted] = useState(false);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">ER Diagrams & Data Modeling</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Master Entity-Relationship diagrams and data modeling techniques
        </p>
      </div>

      <div className="module-card">
        <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">ER Diagram Components</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
            <h4 className="font-bold text-blue-800 dark:text-blue-200 mb-2">Entities</h4>
            <p className="text-gray-700 dark:text-gray-300 text-sm">Real-world objects like Student, Course, Department</p>
          </div>
          <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
            <h4 className="font-bold text-green-800 dark:text-green-200 mb-2">Attributes</h4>
            <p className="text-gray-700 dark:text-gray-300 text-sm">Properties of entities like name, age, ID</p>
          </div>
          <div className="bg-purple-50 dark:bg-purple-900/20 p-4 rounded-lg">
            <h4 className="font-bold text-purple-800 dark:text-purple-200 mb-2">Relationships</h4>
            <p className="text-gray-700 dark:text-gray-300 text-sm">Associations between entities like enrollment</p>
          </div>
        </div>
      </div>

      <div className="text-center mt-8">
        <button
          onClick={() => {
            markTopicComplete('er-diagrams');
            setCompleted(true);
          }}
          className={`btn-secondary px-8 py-3 text-lg ${completed ? 'bg-green-500 hover:bg-green-600' : ''}`}
        >
          {completed ? '✓ Completed' : 'Mark as Complete'}
        </button>
      </div>
    </div>
  );
};

export default ErDiagrams;
