import React, { useState } from 'react';
import { useProgress } from '../../contexts/ProgressContext';

const SignedNumbers = () => {
  const { markTopicComplete } = useProgress();
  const [completed, setCompleted] = useState(false);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Signed Binary Numbers</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Learn different methods to represent signed numbers in binary
        </p>
      </div>

      <div className="space-y-6">
        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Signed Magnitude</h3>
          <p className="text-gray-700 dark:text-gray-300 mb-4">
            First bit represents sign (0 = positive, 1 = negative)
          </p>
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg font-mono">
            0101 = +5<br/>
            1101 = -5
          </div>
        </div>

        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">1's Complement</h3>
          <p className="text-gray-700 dark:text-gray-300 mb-4">
            Invert all bits for negative numbers
          </p>
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg font-mono">
            +5 = 0101<br/>
            -5 = 1010
          </div>
        </div>

        <div className="module-card">
          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">2's Complement</h3>
          <p className="text-gray-700 dark:text-gray-300 mb-4">
            Invert all bits and add 1 for negative numbers (most common)
          </p>
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg font-mono">
            +5 = 0101<br/>
            -5 = 1011
          </div>
        </div>
      </div>

      <div className="text-center mt-8">
        <button
          onClick={() => {
            markTopicComplete('signed-numbers');
            setCompleted(true);
          }}
          className={`btn-primary px-8 py-3 text-lg ${completed ? 'bg-green-500 hover:bg-green-600' : ''}`}
        >
          {completed ? '✓ Completed' : 'Mark as Complete'}
        </button>
      </div>
    </div>
  );
};

export default SignedNumbers;
