import React from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import { useProgress } from '../contexts/ProgressContext';

// SQL/DBMS Modules
import DatabaseFundamentals from './sql-modules/DatabaseFundamentals';
import ErDiagrams from './sql-modules/ErDiagrams';
import KeysNormalization from './sql-modules/KeysNormalization';
import SqlQueries from './sql-modules/SqlQueries';
import AdvancedDbms from './sql-modules/AdvancedDbms';
import RelationalModel from './sql-modules/RelationalModel';

const SqlDbms = () => {
  const location = useLocation();
  const { getOverallProgress } = useProgress();
  const progress = getOverallProgress();

  const modules = [
    {
      id: 'database-fundamentals',
      title: 'Database Fundamentals & Architecture',
      description: 'Learn the basics of databases, their architecture, and key concepts',
      duration: '45 min',
      difficulty: 'Beginner',
      path: 'database-fundamentals',
      icon: 'üíæ',
      color: 'bg-blue-500'
    },
    {
      id: 'relational-model',
      title: 'Relational Model',
      description: 'Master the relational model, keys, constraints, and Codd\'s rules',
      duration: '55 min',
      difficulty: 'Intermediate',
      path: 'relational-model',
      icon: 'üìä',
      color: 'bg-indigo-500'
    },
    {
      id: 'er-diagrams',
      title: 'ER Diagrams & Data Modeling',
      description: 'Master Entity-Relationship diagrams and data modeling techniques',
      duration: '50 min',
      difficulty: 'Intermediate',
      path: 'er-diagrams',
      icon: 'üîÑ',
      color: 'bg-green-500'
    },
    {
      id: 'keys-normalization',
      title: 'Keys & Normalization',
      description: 'Understand different types of keys and normalization concepts in detail',
      duration: '60 min',
      difficulty: 'Advanced',
      path: 'keys-normalization',
      icon: 'üîë',
      color: 'bg-purple-500'
    },
    {
      id: 'sql-queries',
      title: 'SQL Queries & Joins',
      description: 'Master SQL queries, JOIN operations, and complex database operations',
      duration: '55 min',
      difficulty: 'Intermediate',
      path: 'sql-queries',
      icon: 'üîç',
      color: 'bg-orange-500'
    },
    {
      id: 'advanced-dbms',
      title: 'Advanced DBMS Concepts',
      description: 'Explore transactions, concurrency, indexing, and database design patterns',
      duration: '65 min',
      difficulty: 'Advanced',
      path: 'advanced-dbms',
      icon: '‚öôÔ∏è',
      color: 'bg-red-500'
    }
  ];

  const Sidebar = () => (
    <div className="w-full lg:w-64 bg-white dark:bg-card-dark rounded-xl shadow-lg p-6 h-fit">
      <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-6">Modules</h3>
      <nav className="space-y-2">
        {modules.map((module) => (
          <Link
            key={module.id}
            to={module.path}
            className={`flex items-center p-3 rounded-lg transition-all duration-200 ${
              location.pathname.includes(module.path)
                ? 'bg-primary-green text-white shadow-md'
                : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
            }`}
          >
            <span className="text-xl mr-3">{module.icon}</span>
            <div className="flex-1">
              <div className="font-medium">{module.title}</div>
              <div className="text-xs opacity-75">{module.duration}</div>
            </div>
          </Link>
        ))}
      </nav>
    </div>
  );

  const ModuleCard = ({ module }) => {
    const { getTopicProgress } = useProgress();
    const progressData = getTopicProgress(module.id);
    
    return (
      <div className="module-card">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center">
            <div className={`${module.color} w-12 h-12 rounded-lg flex items-center justify-center text-white text-xl mr-4`}>
              {module.icon}
            </div>
            <div>
              <h3 className="text-xl font-bold text-gray-900 dark:text-white">{module.title}</h3>
              <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-400">
                <span>‚è±Ô∏è {module.duration}</span>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  module.difficulty === 'Beginner' ? 'bg-green-100 text-green-800' :
                  module.difficulty === 'Intermediate' ? 'bg-yellow-100 text-yellow-800' :
                  'bg-red-100 text-red-800'
                }`}>
                  {module.difficulty}
                </span>
              </div>
            </div>
          </div>
          {progressData.completed && (
            <div className="text-green-500">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
            </div>
          )}
        </div>
        
        <p className="text-gray-700 dark:text-gray-300 mb-6">{module.description}</p>
        
        <div className="flex justify-between items-center">
          <Link 
            to={module.path}
            className="btn-secondary px-4 py-2 text-sm"
          >
            {progressData.completed ? 'Review' : 'Start Learning'}
          </Link>
          
          {progressData.score > 0 && (
            <div className="text-sm text-gray-600 dark:text-gray-400">
              Score: {progressData.score}%
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <Routes>
        {/* Module routes */}
        <Route path="database-fundamentals" element={<DatabaseFundamentals />} />
        <Route path="relational-model" element={<RelationalModel />} />
        <Route path="er-diagrams" element={<ErDiagrams />} />
        <Route path="keys-normalization" element={<KeysNormalization />} />
        <Route path="sql-queries" element={<SqlQueries />} />
        <Route path="advanced-dbms" element={<AdvancedDbms />} />
        
        {/* Default route - Module overview */}
        <Route path="/" element={
          <div className="space-y-8">
            {/* Header */}
            <div className="bg-gradient-to-r from-primary-green to-green-600 rounded-2xl p-8 text-white">
              <h1 className="text-3xl md:text-4xl font-bold mb-4">SQL & Database Systems</h1>
              <p className="text-xl opacity-90 mb-6">
                Master database design, SQL queries, and advanced DBMS concepts
              </p>
              <div className="flex flex-wrap gap-4">
                <div className="bg-white/20 rounded-lg px-4 py-2">
                  <div className="text-sm opacity-75">Total Modules</div>
                  <div className="text-2xl font-bold">{modules.length}</div>
                </div>
                <div className="bg-white/20 rounded-lg px-4 py-2">
                  <div className="text-sm opacity-75">Your Progress</div>
                  <div className="text-2xl font-bold">{progress.percentage}%</div>
                </div>
                <div className="bg-white/20 rounded-lg px-4 py-2">
                  <div className="text-sm opacity-75">Estimated Time</div>
                  <div className="text-2xl font-bold">4.5 hours</div>
                </div>
              </div>
            </div>

            {/* Modules Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              <div className="lg:col-span-2 space-y-6">
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">Learning Modules</h2>
                <div className="space-y-6">
                  {modules.map((module) => (
                    <ModuleCard key={module.id} module={module} />
                  ))}
                </div>
              </div>
              
              {/* Sidebar */}
              <div className="lg:col-span-1">
                <Sidebar />
              </div>
            </div>
          </div>
        } />
      </Routes>
    </div>
  );
};

export default SqlDbms;
