import { useEffect, useRef } from 'react';
import { 
  animatePageEntrance, 
  animateCard, 
  animateCodeBlock, 
  animateStaggeredList, 
  addHoverAnimation, 
  animateSectionHeader, 
  animateBounce,
  cleanupScrollTriggers 
} from '../utils/gsapAnimations';

export const useGSAPAnimations = () => {
  // Animation for page entrance
  const animatePage = (element) => {
    useEffect(() => {
      if (element?.current) {
        animatePageEntrance(element.current);
      }
    }, [element]);
  };

  // Animation for cards
  const animateCards = (elementsRef) => {
    useEffect(() => {
      if (elementsRef?.current) {
        const elements = Array.isArray(elementsRef.current) 
          ? elementsRef.current 
          : [elementsRef.current];
        
        elements.forEach(element => {
          if (element) animateCard(element);
        });
      }
    }, [elementsRef]);
  };

  // Animation for code blocks
  const animateCodeBlocks = (elementsRef) => {
    useEffect(() => {
      if (elementsRef?.current) {
        const elements = Array.isArray(elementsRef.current) 
          ? elementsRef.current 
          : [elementsRef.current];
        
        elements.forEach(element => {
          if (element) animateCodeBlock(element);
        });
      }
    }, [elementsRef]);
  };

  // Stagger animation for lists
  const animateList = (elementsRef) => {
    useEffect(() => {
      if (elementsRef?.current && elementsRef.current.length > 0) {
        animateStaggeredList(elementsRef.current);
      }
    }, [elementsRef]);
  };

  // Hover animation for elements
  const addHoverEffects = (elementsRef) => {
    useEffect(() => {
      if (elementsRef?.current) {
        const elements = Array.isArray(elementsRef.current) 
          ? elementsRef.current 
          : [elementsRef.current];
        
        elements.forEach(element => {
          if (element) addHoverAnimation(element);
        });
      }
    }, [elementsRef]);
  };

  // Animation for section headers
  const animateHeaders = (elementsRef) => {
    useEffect(() => {
      if (elementsRef?.current) {
        const elements = Array.isArray(elementsRef.current) 
          ? elementsRef.current 
          : [elementsRef.current];
        
        elements.forEach(element => {
          if (element) animateSectionHeader(element);
        });
      }
    }, [elementsRef]);
  };

  // Bounce animation for important elements
  const animateBounceEffect = (element) => {
    useEffect(() => {
      if (element?.current) {
        animateBounce(element.current);
      }
    }, [element]);
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      cleanupScrollTriggers();
    };
  }, []);

  return {
    animatePage,
    animateCards,
    animateCodeBlocks,
    animateList,
    addHoverEffects,
    animateHeaders,
    animateBounceEffect
  };
};
