import React from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import AdvancedQuiz from './AdvancedQuiz';
import { fullComputerArchitectureQuiz } from '../data/computerArchitectureQuiz';
import { fullSqlDbmsQuiz } from '../data/sqlDbmsQuiz';

const QuizCenter = () => {
  const quizzes = [
    {
      title: "Computer Architecture Mastery Quiz",
      description: "Comprehensive 100-question assessment covering all aspects of Computer Architecture including number systems, computer types, architecture types, digital logic, and CPU design",
      subject: "Computer Architecture",
      questions: 100,
      timeLimit: "120 minutes",
      difficulty: "Comprehensive",
      path: "/quiz/computer-architecture",
      color: "bg-blue-500",
      quizData: fullComputerArchitectureQuiz
    },
    {
      title: "SQL & Database Systems Mastery Quiz",
      description: "Complete 100-question examination of SQL queries, database design, normalization, transactions, and advanced DBMS concepts",
      subject: "SQL/DBMS",
      questions: 100,
      timeLimit: "120 minutes",
      difficulty: "Comprehensive",
      path: "/quiz/sql-dbms",
      color: "bg-green-500",
      quizData: fullSqlDbmsQuiz
    },
    {
      title: "Number Systems & Conversions Quiz",
      description: "Focused assessment on binary, decimal, octal, and hexadecimal number systems with conversion techniques",
      subject: "Computer Architecture",
      questions: 25,
      timeLimit: "30 minutes",
      difficulty: "Beginner",
      path: "/quiz/number-systems",
      color: "bg-purple-500"
    },
    {
      title: "Database Fundamentals Quiz",
      description: "Essential concepts of databases, architecture, and basic SQL operations",
      subject: "SQL/DBMS",
      questions: 30,
      timeLimit: "45 minutes",
      difficulty: "Beginner",
      path: "/quiz/database-fundamentals",
      color: "bg-orange-500"
    },
    {
      title: "Keys & Normalization Advanced Quiz",
      description: "Deep dive into database keys, functional dependencies, and all normal forms with practical examples",
      subject: "SQL/DBMS",
      questions: 40,
      timeLimit: "60 minutes",
      difficulty: "Advanced",
      path: "/quiz/keys-normalization",
      color: "bg-red-500"
    }
  ];

  const QuizRoute = ({ quizData }) => {
    const handleQuizComplete = (results) => {
      console.log('Quiz completed with results:', results);
      // Here you can save results to localStorage or send to backend
      localStorage.setItem(`quiz-${quizData.topicId}-results`, JSON.stringify(results));
    };

    return <AdvancedQuiz quizData={quizData} onComplete={handleQuizComplete} />;
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <Routes>
        <Route path="/computer-architecture" element={<QuizRoute quizData={fullComputerArchitectureQuiz} />} />
        <Route path="/sql-dbms" element={<QuizRoute quizData={fullSqlDbmsQuiz} />} />
        <Route path="/" element={
          <>
            <div className="mb-8">
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Quiz Center</h1>
              <p className="text-gray-600 dark:text-gray-300">
                Test your knowledge and track your progress with comprehensive assessments
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {quizzes.map((quiz, index) => (
                <div key={index} className="module-card">
                  <div className="flex items-start mb-4">
                    <div className={`${quiz.color} w-12 h-12 rounded-lg flex items-center justify-center text-white text-xl mr-4`}>
                      {index + 1}
                    </div>
                    <div>
                      <h3 className="text-xl font-bold text-gray-900 dark:text-white">{quiz.title}</h3>
                      <p className="text-gray-600 dark:text-gray-300 text-sm">{quiz.subject}</p>
                    </div>
                  </div>
                  
                  <p className="text-gray-700 dark:text-gray-300 mb-4">{quiz.description}</p>
                  
                  <div className="flex flex-wrap gap-2 mb-4">
                    <span className="px-2 py-1 bg-blue-100 dark:bg-blue-900/30 text-blue-800 dark:text-blue-300 rounded-full text-xs font-medium">
                      üìù {quiz.questions} questions
                    </span>
                    <span className="px-2 py-1 bg-purple-100 dark:bg-purple-900/30 text-purple-800 dark:text-purple-300 rounded-full text-xs font-medium">
                      ‚è±Ô∏è {quiz.timeLimit}
                    </span>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      quiz.difficulty === 'Beginner' ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300' :
                      quiz.difficulty === 'Intermediate' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-300' :
                      quiz.difficulty === 'Advanced' ? 'bg-orange-100 text-orange-800 dark:bg-orange-900/30 dark:text-orange-300' :
                      'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300'
                    }`}>
                      {quiz.difficulty}
                    </span>
                  </div>
                  
                  <Link 
                    to={quiz.path}
                    className={`btn-primary px-4 py-2 text-sm w-full text-center ${
                      quiz.questions >= 100 ? 'bg-gradient-to-r from-primary-blue to-primary-green' : ''
                    }`}
                  >
                    {quiz.questions >= 100 ? 'üöÄ Start Comprehensive Quiz' : 'Start Quiz'}
                  </Link>
                </div>
              ))}
            </div>

            {/* Quiz Features */}
            <div className="mt-16 bg-white dark:bg-card-dark rounded-xl shadow-lg p-8">
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6 text-center">
                Advanced Quiz Features
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                <div className="text-center">
                  <div className="w-16 h-16 bg-blue-100 dark:bg-blue-900/30 rounded-full flex items-center justify-center mx-auto mb-4">
                    <span className="text-2xl">‚è±Ô∏è</span>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">Timed Assessment</h3>
                  <p className="text-gray-600 dark:text-gray-300">
                    Real exam conditions with countdown timers and automatic submission
                  </p>
                </div>
                
                <div className="text-center">
                  <div className="w-16 h-16 bg-green-100 dark:bg-green-900/30 rounded-full flex items-center justify-center mx-auto mb-4">
                    <span className="text-2xl">üìä</span>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">Detailed Analytics</h3>
                  <p className="text-gray-600 dark:text-gray-300">
                    Comprehensive results with score breakdown, time analysis, and performance metrics
                  </p>
                </div>
                
                <div className="text-center">
                  <div className="w-16 h-16 bg-purple-100 dark:bg-purple-900/30 rounded-full flex items-center justify-center mx-auto mb-4">
                    <span className="text-2xl">üîç</span>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">Answer Review</h3>
                  <p className="text-gray-600 dark:text-gray-300">
                    Detailed review of all answers with explanations and correct solutions
                  </p>
                </div>
              </div>
            </div>
          </>
        } />
      </Routes>
    </div>
  );
};

export default QuizCenter;
